export default function ShowAndHide(){if(document.querySelector(".filter_categories")||null){var t=document.querySelector(".btn-store");let e=document.querySelector(".filter_categories");t.addEventListener("click",()=>{e.classList.contains("show")?e.classList.remove("show"):e.classList.add("show")})}t=document.querySelector(".arrow_down");let e=document.querySelectorAll(".phone_item"),o=document.querySelector(".header_phone");document.querySelector("body");t.addEventListener("click",()=>{o.classList.toggle("phone_active"),e.forEach(t=>{t.classList.toggle("phone_item_active"),document.addEventListener("click",e=>{o.contains(e.target)||(t.classList.remove("phone_item_active"),o.classList.remove("phone_active"))})})})}